import React, { useState } from 'react';
import axios from 'axios';
import { Send } from 'lucide-react';

const HotelChatbot = () => {
  const [roomNumber, setRoomNumber] = useState('');
  const [surname, setSurname] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');

  const handleAuthentication = (e) => {
    e.preventDefault();
    setIsAuthenticated(true);
  };

  const handleSendMessage = async () => {
    if (currentMessage.trim() === '') return;

    const newMessage = { text: currentMessage, isUser: true };
    setMessages([...messages, newMessage]);
    setCurrentMessage('');

    try {
      const response = await axios.post('https://dummy.dummy.com/endpoint', {
        message: currentMessage,
        roomNumber,
        surname,
      });

      setMessages((prevMessages) => [
        ...prevMessages,
        { text: response.data.reply, isUser: false },
      ]);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      {!isAuthenticated ? (
        <div className="flex-grow flex items-center justify-center">
          <form onSubmit={handleAuthentication} className="bg-white p-8 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold mb-4 text-center">Welcome to Hotel Chat</h2>
            <input
              type="text"
              value={roomNumber}
              onChange={(e) => setRoomNumber(e.target.value)}
              placeholder="Room Number"
              className="w-full p-2 mb-4 border rounded"
              required
            />
            <input
              type="text"
              value={surname}
              onChange={(e) => setSurname(e.target.value)}
              placeholder="Surname"
              className="w-full p-2 mb-4 border rounded"
              required
            />
            <button
              type="submit"
              className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition-colors"
            >
              Start Chat
            </button>
          </form>
        </div>
      ) : (
        <>
          <div className="flex-grow overflow-auto p-4">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`mb-4 ${
                  message.isUser ? 'text-right' : 'text-left'
                }`}
              >
                <span
                  className={`inline-block p-2 rounded-lg ${
                    message.isUser
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-300 text-black'
                  }`}
                >
                  {message.text}
                </span>
              </div>
            ))}
          </div>
          <div className="p-4 bg-white">
            <div className="flex items-center">
              <input
                type="text"
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-grow p-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              />
              <button
                onClick={handleSendMessage}
                className="bg-blue-500 text-white p-2 rounded-r hover:bg-blue-600 transition-colors"
              >
                <Send size={24} />
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default HotelChatbot;
